      var langSelect = document.getElementById('lang-select');
      var currentLang = localStorage.getItem('lang') || 'vi';
      if (langSelect) {
        langSelect.value = currentLang;
      }

      function changeLanguage(lang) {
        var navLinks = document.querySelectorAll('nav a');
        if (navLinks.length >= 5) {
          navLinks[0].textContent = translations[lang].home;
          navLinks[1].textContent = translations[lang].services;
          navLinks[2].textContent = translations[lang].pricing;
          navLinks[3].textContent = translations[lang].testimonials;
          navLinks[4].textContent = translations[lang].faq;
        }

        var heroTitle = document.querySelector('.hero h1');
        var heroDesc = document.querySelector('.hero p');
        var btns = document.querySelectorAll('.btn');

        if (heroTitle) heroTitle.textContent = translations[lang].heroTitle;
        if (heroDesc) heroDesc.textContent = translations[lang].heroDesc;
        if (btns.length >= 2) {
          btns[0].textContent = translations[lang].exploreBtn;
          btns[1].textContent = translations[lang].pricingBtn;
        }
      }

      changeLanguage(currentLang);

      if (langSelect) {
        langSelect.addEventListener('change', function (e) {
          var lang = e.target.value;
          localStorage.setItem('lang', lang);
          changeLanguage(lang);
          showToast('success', 'Th√†nh c√¥ng!', 'ƒê√£ thay ƒë·ªïi ng√¥n ng·ªØ');
        });
      }

      // Scroll Progress Bar
      window.addEventListener('scroll', function () {
        var winScroll = document.body.scrollTop || document.documentElement.scrollTop;
        var height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
        var scrolled = (winScroll / height) * 100;
        var progressBar = document.getElementById('scroll-progress');
        if (progressBar) {
          progressBar.style.width = scrolled + '%';
        }

        var header = document.getElementById('header');
        if (header) {
          if (winScroll > 100) {
            header.classList.add('scrolled');
          } else {
            header.classList.remove('scrolled');
          }
        }
      });

      // Intersection Observer
      var observer = new IntersectionObserver(
        function (entries) {
          entries.forEach(function (entry) {
            if (entry.isIntersecting) {
              entry.target.classList.add('visible');
            }
          });
        },
        { threshold: 0.1 }
      );

      var sections = document.querySelectorAll('.services, .testimonials, .faq, .pricing');
      sections.forEach(function (section) {
        observer.observe(section);
      });

      // Theme Toggle
      var toggle = document.getElementById('theme-toggle');
      var currentTheme = localStorage.getItem('theme');
      if (currentTheme === 'dark') {
        document.body.classList.add('dark');
        if (toggle) {
          toggle.innerHTML = '<i class="fas fa-sun"></i>';
        }
      }

      if (toggle) {
        toggle.addEventListener('click', function () {
          document.body.classList.toggle('dark');
          var theme = document.body.classList.contains('dark') ? 'dark' : 'light';
          localStorage.setItem('theme', theme);
          toggle.innerHTML = theme === 'dark' ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
          showToast('success', 'ƒê√£ thay ƒë·ªïi!', 'Ch·∫ø ƒë·ªô ' + (theme === 'dark' ? 't·ªëi' : 's√°ng') + ' ƒë√£ ƒë∆∞·ª£c k√≠ch ho·∫°t');
        });
      }

      // Menu toggle handler
      var menuToggleBtn = document.querySelector('.menu-toggle');
      var menuCheckboxInput = document.getElementById('menu-checkbox');
      if (menuToggleBtn && menuCheckboxInput) {
        menuToggleBtn.addEventListener('click', function (e) {
          e.preventDefault();
          e.stopPropagation();
          menuCheckboxInput.checked = !menuCheckboxInput.checked;
        });
      }

      // Close menu when clicking outside (Mobile)
      document.addEventListener('click', function (event) {
        var menuCheckbox = document.getElementById('menu-checkbox');
        var menuToggle = document.querySelector('.menu-toggle');
        var nav = document.querySelector('#main-nav');

        if (window.innerWidth <= 768 && menuCheckbox && menuCheckbox.checked) {
          if (!menuToggle.contains(event.target) && !nav.contains(event.target)) {
            menuCheckbox.checked = false;
          }
        }
      });

      // Close menu on link click (Mobile)
      document.querySelectorAll('nav a').forEach(function (link) {
        link.addEventListener('click', function () {
          var menuCheckbox = document.getElementById('menu-checkbox');
          if (menuCheckbox && window.innerWidth <= 768) {
            menuCheckbox.checked = false;
          }
        });
      });

      // FAQ Accordion
      var faqQuestions = document.querySelectorAll('.faq-question');
      faqQuestions.forEach(function (question) {
        question.addEventListener('click', function () {
          var faqItem = question.parentElement;
          var wasActive = faqItem.classList.contains('active');

          var allFaqItems = document.querySelectorAll('.faq-item');
          allFaqItems.forEach(function (item) {
            item.classList.remove('active');
          });

          if (!wasActive) {
            faqItem.classList.add('active');
          }
        });
      });

      // Stats Counter
      var statNumbers = document.querySelectorAll('.stat-number');
      var hasAnimated = false;
      var statsObserver = new IntersectionObserver(
        function (entries) {
          entries.forEach(function (entry) {
            if (entry.isIntersecting && !hasAnimated) {
              hasAnimated = true;
              statNumbers.forEach(function (stat) {
                var target = parseFloat(stat.getAttribute('data-target'));
                var isDecimal = target % 1 !== 0;
                var increment = target / 100;
                var current = 0;

                var updateCount = function () {
                  if (current < target) {
                    current += increment;
                    if (isDecimal) {
                      stat.textContent = current.toFixed(1);
                    } else {
                      stat.textContent = Math.ceil(current).toLocaleString();
                    }
                    setTimeout(updateCount, 20);
                  } else {
                    if (isDecimal) {
                      stat.textContent = target.toFixed(1) + '%';
                    } else {
                      stat.textContent = target.toLocaleString() + '+';
                    }
                  }
                };
                updateCount();
              });
            }
          });
        },
        { threshold: 0.5 }
      );

      var statsSection = document.querySelector('.stats');
      if (statsSection) {
        statsObserver.observe(statsSection);
      }

      // Back to Top Button
      var backToTop = document.getElementById('back-to-top');
      window.addEventListener('scroll', function () {
        if (backToTop) {
          if (window.pageYOffset > 300) {
            backToTop.classList.add('visible');
          } else {
            backToTop.classList.remove('visible');
          }
        }
      });
      if (backToTop) {
        backToTop.addEventListener('click', function () {
          window.scrollTo({ top: 0, behavior: 'smooth' });
        });
      }

      // Chat Button
