          var emailInput = e.target.querySelector('input');
          var email = emailInput ? emailInput.value : '';
          showToast('success', 'ƒêƒÉng k√Ω th√†nh c√¥ng!', 'C·∫£m ∆°n b·∫°n ƒë√£ ƒëƒÉng k√Ω v·ªõi email: ' + email);
          e.target.reset();
        });
      }

      // Toast Notification Function
      function showToast(type, title, message) {
        var toast = document.getElementById('toast');
        var toastTitle = document.getElementById('toast-title');
        var toastMessage = document.getElementById('toast-message');

        if (!toast || !toastTitle || !toastMessage) return;
        toast.className = 'toast ' + type;
        toastTitle.textContent = title;
        toastMessage.textContent = message;

        var icon = toast.querySelector('i');
        if (icon) {
          if (type === 'success') {
            icon.className = 'fas fa-check-circle';
          } else {
            icon.className = 'fas fa-exclamation-circle';
          }
        }

        toast.classList.add('show');
        setTimeout(function () {
          toast.classList.remove('show');
        }, 3000);
      }

      // Pricing Button Click
      var pricingBtns = document.querySelectorAll('.pricing-btn');
      pricingBtns.forEach(function (btn) {
        btn.addEventListener('click', function () {
          showToast('success', 'Tuy·ªát v·ªùi!', 'Vui L√≤ng Li√™n h·ªá support@mskdns.tokyo ƒë·ªÉ ƒë·∫∑t h√†ng');
        });
      });

      // Smooth scroll for anchor links
      var anchorLinks = document.querySelectorAll('a[href^="#"]');
      anchorLinks.forEach(function (anchor) {
        anchor.addEventListener('click', function (e) {
          e.preventDefault();
          var href = this.getAttribute('href');
          if (href && href !== '#') {
            var target = document.querySelector(href);
            if (target) {
              target.scrollIntoView({
                behavior: 'smooth',
                block: 'start',
              });
            }
          }
        });
      });
